import React, { Component } from 'react';
import api from './api';

class App extends Component{

  state = {
    filmes: [],
  }

  async componentDidMount(){

    const response = await api.get('/');

    this.setState({filmes: response.data});
  }

  render(){

    const {filmes} = this.state;
    console.log(filmes)

    return(
      <div>
        <h1>Listar filmes</h1>
        <ul style={{listStyleType: 'none', margin: 0, padding: 0}}>
          {filmes.map(filmes => (
            <li key={filmes._id}>
              <h2>Titulo: {filmes.titulo}</h2>
              <p>Diretor: {filmes.director}</p>
              <p>GÃªnero: {filmes.gender}</p>
              <p>Votos: {filmes.vote} </p>
            </li>
          ))}
        </ul>
      </div>
    )
  }
}

export default App;
