import React, { useState } from 'react';
import styled from 'styled-components';
import { useN } from 'react-router-dom';
import { InputGroup, FormControl, Button, Form, Col } from 'react-bootstrap';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import api from '../api';

const SearchHeader = styled.div`
    margin-top: 4px;
`;



function SearchBar(props) {

    const [search, setSearch] = useState('');

    const history = useNavigate();

    function newFilme() {
        history.push('/filme/new');
    }

    function handleOnSubmit(event) {
        event.preventDefault();
        const filme = api.get('');
        const results = filme.filter(filme => filme.title.toLowerCase().indexOf(search) !== -1);
        props.setFilme(results);
    }

    function handleSearchChange(event) {
        setSearch(event.target.value.toLowerCase());
    }

    return (
        <SearchHeader>
            <Form onSubmit={handleOnSubmit}>
                <Form.Row>
                    <Col className="col-xl-3">
                        <InputGroup className="mb-3">
                            <FormControl
                                placeholder="Search films"
                                aria-label="Search films"
                                onChange={handleSearchChange}
                            />
                            <InputGroup.Append>
                                <Button type="submit">
                                    <FontAwesomeIcon icon={faSearch} />
                                </Button>
                            </InputGroup.Append>
                        </InputGroup>
                    </Col>
                    <Col className="col-xl-2">
                        <Form.Group controlId="order">
                            <Form.Control as="select">
                                <option>Newest</option>
                                <option>Oldest</option>
                            </Form.Control>
                        </Form.Group>
                    </Col>
                    <Col>
                        <Button variant="primary" className="float-right" onClick={newFilme}>
                            New Film
                            </Button>
                    </Col>
                </Form.Row>
            </Form>
        </SearchHeader>
    );
}

export default SearchBar;
